import { MouseEvent as ReactMouseEvent, SVGProps, FunctionComponent } from 'react';
export interface SimpleButtonProps {
    /**
     * This props represent the text content of the button.
     */
    label: string;
    /**
     * This props represent the function that will be execute on the click event.
     * If the type of the button is set to ``submit`` this props will be optional.
     */
    onClick?: (event: ReactMouseEvent<HTMLButtonElement, MouseEvent>) => void;
    /**
     * This props represent the type of the button. if is set to ``submit`` it must be inside
     * a form tag.
     */
    type: 'button' | 'submit';
    buttonColor: 'orange' | 'green' | 'white' | 'black' | 'outlined' | 'success' | 'warning' | 'error' | 'info';
    isSubmitting?: boolean;
    disabled?: boolean;
    icon?: FunctionComponent<SVGProps<SVGSVGElement>>;
    classes?: string[];
    testID?: string;
}
